import React from "react";
import { Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import NotFound from "./pages/NotFound";
import Cart from "./pages/Cart";
import FullPizza from "./pages/FullPizza";

const OtherComponent = React.lazy(() => import('./pages/Cart'));

import "./scss/app.scss";
import MainLayout from "./layouts/MainLayout";

type ContextProp = { serachValue: string; setSerachValue: (str: string) => void };

export const SearchContext = React.createContext<ContextProp>({
  serachValue: "",
  setSerachValue: () => {},
});

function App() {
  const [serachValue, setSerachValue] = React.useState("");

  return (
    <SearchContext.Provider value={{ serachValue, setSerachValue }}>
      <Routes>
        <Route path="/" element={<MainLayout />}>
          <Route path="" element={<Home />} />
          <Route path="cart" element={<Cart />} />
          <Route path="pizza/:id" element={<FullPizza />} />
          <Route path="*" element={<NotFound />} />
        </Route>
      </Routes>
    </SearchContext.Provider>
  );
}

export default App;
