import React from "react";
import debounce from "lodash.debounce";

import { SearchContext } from "../../App";

const testDebounce = debounce(() =>{
  console.log()
})


import styles from "./Search.module.scss";
const Search = () => {
  const {serachValue, setSerachValue} = React.useContext(SearchContext);
  const inputRef = React.useRef();
  
  const onClickClear = () => {
    setSerachValue('');
    inputRef.current.focus();
  }

  return (
    <div className={styles.root}>
      <svg
        className={styles.icon}
        height="22"
        viewBox="0 0 512 512"
        width="22"
        xmlns="http://www.w3.org/2000/svg"
      >
        <title />
        <path d="M464,428,339.92,303.9a160.48,160.48,0,0,0,30.72-94.58C370.64,120.37,298.27,48,209.32,48S48,120.37,48,209.32s72.37,161.32,161.32,161.32a160.48,160.48,0,0,0,94.58-30.72L428,464ZM209.32,319.69A110.38,110.38,0,1,1,319.69,209.32,110.5,110.5,0,0,1,209.32,319.69Z" />
      </svg>
      <input
      ref={inputRef}
        value={serachValue}
        onChange={(event) => setSerachValue(event.target.value)}
        className={styles.input}
        placeholder="Поиск пиццы..."
      />
      {serachValue && (
        <svg
          className={styles.clearIcon}
          onClick={onClickClear}
          height="18"
          viewBox="0 0 48 48"
          width="18"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path d="M38 12.83l-2.83-2.83-11.17 11.17-11.17-11.17-2.83 2.83 11.17 11.17-11.17 11.17 2.83 2.83 11.17-11.17 11.17 11.17 2.83-2.83-11.17-11.17z" />
          <path d="M0 0h48v48h-48z" fill="none" />
        </svg>
      )}
    </div>
  );
};

export default Search;
